import React, { useState } from "react";
import ColorPalleteItems from "./ColorPalleteItems";

function AddColorForm({ post, deleteShade, shades, addColor, colors, searchTerm, background, setBackground }) {
    const [formData, setFormData] = useState({
        hex_code: "",
        color_id: ""
    });

    // const handleInputChange = (e) => {
    //     setFormData({ ...formData, [e.target.name]: e.target.value });
    //     console.log(e.target.value)
    // };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
        console.log()
      };

    const onSubmit = (e) => {
        e.preventDefault();
        const newData = { ...formData };
        post(newData);
    };

    return (
        <div style={{ textAlign: "center", paddingTop: "100px" }} >
            <form onSubmit={onSubmit} >

                <input type="text" name="hex_code" placeholder="Add Shade" onChange={handleInputChange} className="hi" />
                <input type="text" name="color_id" placeholder="Add Color" onChange={handleInputChange} className="hi" />

                <button type="submit" className="button-12" style={{ margin: "15px", borderRadius: "60px", width: "100px", height: "27px", cursor: "pointer" }}>
                    Submit
                </button>
            </form>
            <ColorPalleteItems deleteShade={deleteShade} colors={colors} shades={shades} searchTerm={searchTerm} background={background} setBackground={setBackground} />
        </div>
    );
}

export default AddColorForm;
