import './App.css';
import React, { useEffect, useState } from 'react';

function App() {
  const [shades, setShades] = useState([]);
  const [background, setBackground] = useState("#071415");
  const [searchTerm, setSearchTerm] = useState("");

  const shToShow = shades.filter((sh) =>
        sh.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

  useEffect(() => {
    fetch("http://localhost:9292/shades")
      .then((response) => response.json())
      .then((data) => {
        setShades(data);
        console.log(data)
      });
  }, []);


  return (
    <div style={{ background: background, paddingTop: '35px' }}>
      <div className="container">
        {shades.map((shade) => (
          <div key={shade.id} className="card" >
            <div style={{
              background: shade.hex_code,
              filter: "brightness(85%)",
              boxShadow: shade.hex_code === background ? "0 0 5px #000" : ""
            }}
              className="box"
              onClick={() => setBackground(shade.hex_code)}>
            </div>
          </div>))}</div></div>)
}




export default App;
